import React from "react";
// react component for creating beautiful carousel
import Carousel from "react-slick";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// @material-ui/icons
import LocationOn from "@material-ui/icons/LocationOn";
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Card from "components/Card/Card.js";

import image1 from "assets/img/blogs/Marikana1.jpeg";
import image2 from "assets/img/blogs/Marikana2.jpg";

import styles from "assets/jss/material-kit-react/views/componentsSections/carouselStyle.js";

const useStyles = makeStyles(styles);

export default function SectionCarousel(props) {
  const classes = useStyles();
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false
  };
  const carouselImages = props.carouselImages;
  const keyNum = 0
  return (

    <div className={classes.section}>
      <div className={classes.container}>
        <GridContainer>
        {
                carouselImages.map(image => {
                  return (
          <GridItem xs={12} sm={12} md={8} className={classes.marginAuto} key={image}>
            <Card carousel>
              <Carousel {...settings}>

                    <div>
                      <img
                        src={image}
                        alt={image}
                        className="slick-image"
                      />
                      <div className="slick-caption"></div>
                    </div>

              </Carousel>
            </Card>
          </GridItem>
            );
          })}
        </GridContainer>
      </div>
    </div>
  );
}
