// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";

import styles from "assets/jss/material-kit-react/views/landingPageSections/workStyle.js";
import SectionCarousel from "./SectionCarousel";
import { getBlogPostById } from "./paragraphContext.js";

const useStyles = makeStyles(styles);

export default function BlogParagraphs() {
  const classes = useStyles();
  const {paragraphs,
    mainTitle,
    carouselImages
  } = this.

  blogItem
  return (
    <div className={classes.section}>
      <GridContainer justify="center">
        <GridItem cs={12} sm={12} md={8}>
          {<h2 className={classes.title}>{blogItem.mainTitle}</h2>}
          {blogItem.paragraphs.map(paragraph => {
            return <h4 className={classes.description}>{paragraph}</h4>;
          })}
        </GridItem>
      </GridContainer>
      <SectionCarousel carouselImages={blogItem.carouselImages}/>
    </div>
  );
}
