import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";

// core components
import styles from "../../assets/jss/material-kit-react/components/parallaxStyle.js";
import SectionCarousel from "../../views/Components/Sections/SectionCarousel.js";



import image1 from "../../assets/img/bioscope1.jpg";
import image2 from "../../assets/img/bg2.jpg";
import image3 from "../../assets/img/bg3.jpg";
import Carousel from "react-slick";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";

//import styles from "../../assets/jss/material-kit-react/views/componentsSections/carouselStyle.js";

const useStyles = makeStyles(styles);

export default function Parallax(props) {
  let windowScrollTop;
  if (window.innerWidth >= 768) {
    windowScrollTop = window.pageYOffset / 3;
  } else {
    windowScrollTop = 0;
  }
  const [transform, setTransform] = React.useState(
    "translate3d(0," + windowScrollTop + "px,0)"
  );
  React.useEffect(() => {
    if (window.innerWidth >= 768) {
      window.addEventListener("scroll", resetTransform);
    }
    return function cleanup() {
      if (window.innerWidth >= 768) {
        window.removeEventListener("scroll", resetTransform);
      }
    };
  });
  const resetTransform = () => {
    var windowScrollTop = window.pageYOffset / 3;
    setTransform("translate3d(0," + windowScrollTop + "px,0)");
  };
  const { filter, className, children, style, image, small } = props;
  const classes = useStyles();
  const parallaxClasses = classNames({
    [classes.parallax]: true,
    [classes.filter]: filter,
    [classes.small]: small,
    [className]: className !== undefined
  });

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false
  };
  return (

    <Carousel {...settings}>
    <div>
      <img src={image1} alt="First slide" className="slick-image" />
      <div className="slick-caption">
          <GridContainer>
            <GridItem>
              <div className={classes.brand} >
                <h1 className={classes.title}>Vision</h1>
                <h3 className={classes.subtitle}>
                  Share african stories for africans
                </h3>
              </div>
            </GridItem>
          </GridContainer>
        </div>
    </div>
    <div>
      <img src={image2} alt="Second slide" className="slick-image" />
      <div className="slick-caption">
          <GridContainer>
            <GridItem>
              <div className={classes.brand} >
                <h1 className={classes.title}>Mission</h1>
                <h3 className={classes.subtitle}>
                  Create change through Art
                </h3>
              </div>
            </GridItem>
          </GridContainer>
        </div>
    </div>
    <div>
      <img src={image3} alt="Third slide" className="slick-image" />
      <div className="slick-caption">
          <GridContainer>
            <GridItem>
              <div className={classes.brand} >
                <h1 className={classes.title}>iBioscope yeCommunity</h1>
                <h3 className={classes.subtitle}>
                  Film accessibility
                </h3>
              </div>
            </GridItem>
          </GridContainer>
        </div>
    </div>

  </Carousel>

    /*<div
      className={parallaxClasses}
      style={{
        ...style,
        backgroundImage: "url(" + image + ")",
        transform: transform
      }}
    >
      {children}
    </div>*/
  );
}

Parallax.propTypes = {
  className: PropTypes.string,
  filter: PropTypes.bool,
  children: PropTypes.node,
  style: PropTypes.string,
  image: PropTypes.string,
  small: PropTypes.bool
};
